#include "WhereClause.hpp"


using std::string;


namespace bgws {


void WhereClause::add( const std::string& s )
{
    if ( _s.empty() ) {
        _s = " WHERE (";
    } else {
        _s += " AND (";
    }
    _s += s;
    _s += ")";
}


} // namespace bgws
